import React, { useState, useEffect } from "react";
import {
  useCreateFlightTypeMutation,
  useUpdateFlightTypeMutation,
} from "../../redux/api/flightTypeApi";
import toast from "react-hot-toast";

const FlightTypeForm = ({ selectedFlight, onSuccess }) => {
  const [flightData, setFlightData] = useState({
    name: "",
    capacity: "",
    description: "",
    image: "",
    maxSpeed: "",
    status: true,
  });

  useEffect(() => {
    if (selectedFlight) setFlightData(selectedFlight);
  }, [selectedFlight]);

  const [createFlightType] = useCreateFlightTypeMutation();
  const [updateFlightType] = useUpdateFlightTypeMutation();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedFlight) {
        await updateFlightType({ id: selectedFlight._id, ...flightData });
        toast.success("Flight type updated");
      } else {
        await createFlightType(flightData);
        toast.success("Flight type created");
      }
      onSuccess();
      setFlightData({
        name: "",
        capacity: "",
        description: "",
        image: "",
        maxSpeed: "",
        status: true,
      });
    } catch (err) {
      toast.error("Error saving flight type");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="card card-body mb-4">
      <input
        type="text"
        placeholder="Name"
        className="form-control mb-2"
        value={flightData.name}
        onChange={(e) => setFlightData({ ...flightData, name: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="Capacity"
        className="form-control mb-2"
        value={flightData.capacity}
        onChange={(e) =>
          setFlightData({ ...flightData, capacity: e.target.value })
        }
        required
      />
      <textarea
        placeholder="Description"
        className="form-control mb-2"
        value={flightData.description}
        onChange={(e) =>
          setFlightData({ ...flightData, description: e.target.value })
        }
      ></textarea>
      <input
        type="text"
        placeholder="Image URL"
        className="form-control mb-2"
        value={flightData.image}
        onChange={(e) =>
          setFlightData({ ...flightData, image: e.target.value })
        }
      />
      <input
        type="number"
        placeholder="Max Speed"
        className="form-control mb-2"
        value={flightData.maxSpeed}
        onChange={(e) =>
          setFlightData({ ...flightData, maxSpeed: e.target.value })
        }
      />
      <select
        className="form-select mb-2"
        value={flightData.status}
        onChange={(e) =>
          setFlightData({ ...flightData, status: e.target.value === "true" })
        }
      >
        <option value="true">Active</option>
        <option value="false">Inactive</option>
      </select>
      <button className="btn btn-primary" type="submit">
        {selectedFlight ? "Update" : "Add"} Flight
      </button>
    </form>
  );
};

export default FlightTypeForm;
