import React, { useEffect, useState, useRef } from "react";
import {
  useCreateBookingMutation,
  useUpdateBookingMutation,
} from "../../redux/api/bookingApi";
import { useGetUsersQuery } from "../../redux/api/adminApi";
import { useGetFlightTypesQuery } from "../../redux/api/flightTypeApi";
import { useGetEmptyLegsQuery } from "../../redux/api/emptyLegApi"; // NEW: fetch empty legs
import { useGetAirportsQuery } from "../../redux/api/airportApi";
import AsyncSelect from "react-select/async";
import Select from "react-select";
import toast from "react-hot-toast";
// import { airports } from "airports-json";
import EmptyLegModal from "../bookings/EmptyLegModal";

const getAirportLabel = (iata) => {
  if (!iata) return "";
  const airport = airports.find((a) => a.iata === iata || a.iata_code === iata);
  if (airport) {
    return `${airport.name} (${airport.iata || airport.iata_code}) - ${
      airport.municipality || "Unknown City"
    }`;
  }
  return iata;
};

const AddBookingForm = ({ setShowForm, refetch, booking }) => {
  const formRef = useRef(null);
  const [form, setForm] = useState({
    user: "",
    fromPlace: "",
    toPlace: "",
    travelDate: "",
    travelTime: "",
    returnDate: "",
    returnTime: "",
    flightType: "",
    pax: "",
    status: "pending",
    remarks: "",
    totalAmount: "",
    bookingType: "normal",
    tripType: "oneway", // keep tripType present
    emptyLegId: "", // store selected empty leg id (if bookingType === 'empty_legs')
  });

  const [showEmptyLegModal, setShowEmptyLegModal] = useState(false);
  const [emptyLegsModalList, setEmptyLegsModalList] = useState([]);

  const { data: users } = useGetUsersQuery();
  const { data: flightTypes } = useGetFlightTypesQuery();
  const { data: emptyLegsData } = useGetEmptyLegsQuery(); // NEW
  const [createBooking, { isLoading: isCreating }] = useCreateBookingMutation();
  const [updateBooking, { isLoading: isUpdating }] = useUpdateBookingMutation();
  const { data: airportsData } = useGetAirportsQuery();
  const airportOptions =
    airportsData?.airports?.map((a) => ({
      value: a.iataCode,
      label: `${a.airportName} (${a.iataCode}) - ${a.city}`,
    })) || [];
  const isLoading = isCreating || isUpdating; // combined loader state

  // Prefill when editing
  useEffect(() => {
    if (booking) {
      setForm({
        user: booking.user?._id || "",
        fromPlace: booking.fromPlace || "",
        toPlace: booking.toPlace || "",
        travelDate: booking.travelDate || "",
        travelTime: booking.travelTime || "",
        returnDate: booking.returnDate || "",
        returnTime: booking.returnTime || "",
        flightType: booking.flightType?._id || booking.flightType || "",
        pax: booking.pax || "",
        status: booking.status || "pending",
        remarks: booking.remarks || "",
        totalAmount: booking.totalAmount || 0,
        bookingType: booking.bookingType || "normal",
        tripType: booking.tripType || "oneway",
        emptyLegId: booking.emptyLegId || "",
      });
      formRef.current?.scrollIntoView({ behavior: "smooth" });
    }
  }, [booking]);

  // Airport search
  const searchAirports = (inputValue) => {
    if (!inputValue) return [];
    return airports
      .filter(
        (a) =>
          a.name?.toLowerCase().includes(inputValue.toLowerCase()) ||
          a.municipality?.toLowerCase().includes(inputValue.toLowerCase()) ||
          a.iata_code?.toLowerCase().includes(inputValue.toLowerCase())
      )
      .slice(0, 20)
      .map((a) => ({
        value: a.iata_code,
        label: `${a.name} (${a.iata_code}) - ${
          a.municipality || "Unknown City"
        }`,
      }));
  };

  // AsyncSelect loader
  const loadAirportOptions = (inputValue, callback) => {
    setTimeout(() => {
      callback(searchAirports(inputValue));
    }, 300);
  };

  const userOptions =
    users?.users?.map((u) => ({
      value: u._id,
      label: `${u.name} (${u.email})`,
    })) || [];

  const flightOptions =
    flightTypes?.flightTypes?.map((f) => ({
      value: f._id,
      label: `${f.name} (${f.capacity} seats)`,
    })) || [];

  // prepare emptyLeg options for a select and modal
  const emptyLegs = (emptyLegsData?.emptyLegs || []).map((leg) => ({
    value: leg._id,
    label: `${leg.from} → ${leg.to} — ₹${Number(
      leg.offerPrice || leg.exactPrice || 0
    ).toLocaleString()}`,
    leg,
  }));

  // When admin selects an empty leg from the select
  const handleEmptyLegSelect = (option) => {
    if (!option) {
      // clear
      setForm((prev) => ({
        ...prev,
        emptyLegId: "",
        fromPlace: "",
        toPlace: "",
        travelDate: "",
        travelTime: "",
        flightType: "",
        totalAmount: "",
      }));
      return;
    }
    const leg = option.leg;
    setForm((prev) => ({
      ...prev,
      emptyLegId: leg._id,
      bookingType: "empty_legs",
      fromPlace: leg.from,
      toPlace: leg.to,
      travelDate: leg.date,
      travelTime: leg.time,
      flightType: leg.flightType?._id || prev.flightType,
      totalAmount: leg.offerPrice || leg.exactPrice || prev.totalAmount,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (form.bookingType === "empty_legs") {
      // require emptyLegId for empty legs
      if (!form.emptyLegId) {
        toast.error("Please select an Empty Leg deal.");
        return;
      }
    }

    // required fields for both types (some fields auto-filled for empty_legs)
    const requiredFields = [
      "user",
      "fromPlace",
      "toPlace",
      "travelDate",
      "travelTime",
      "flightType",
      "pax",
      "totalAmount",
    ];
    for (const key of requiredFields) {
      if (!form[key] && form[key] !== 0) {
        toast.error("Please fill all required fields.");
        return;
      }
    }

    // If round trip, require return date/time
    if (form.tripType === "round" && (!form.returnDate || !form.returnTime)) {
      toast.error("Please enter return date and time for round trip");
      return;
    }

    try {
      const payload = {
        ...form,
        totalAmount: parseFloat(form.totalAmount),
      };

      if (booking?._id) {
        const res = await updateBooking({
          id: booking._id,
          data: payload,
        }).unwrap();
        // if backend returns suggested empty legs (your earlier logic), open modal
        // Show empty-leg suggestions ONLY for normal bookings
        if (
          form.bookingType === "normal" &&
          res?.emptyLegs &&
          res.emptyLegs.length > 0
        ) {
          setEmptyLegsModalList(res.emptyLegs);
          setShowEmptyLegModal(true);
          return;
        }
      } else {
        await createBooking(payload).unwrap();
      }

      toast.success(`Booking ${booking ? "updated" : "created"} successfully`);
      refetch();
      setShowForm(false);
    } catch (err) {
      console.error("Error submitting form", err);
      toast.error(err?.data?.message || "Failed to create booking");
    }
  };

  return (
    <>
      {/* Loader */}
      {isLoading && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(255,255,255,0.8)",
            zIndex: 9999,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}

      <form
        ref={formRef}
        onSubmit={handleSubmit}
        className="border p-3 mb-4 bg-light"
      >
        <div className="row g-3">
          {/* Traveler */}
          <div className="col-md-4">
            <label>Name</label>
            <Select
              options={userOptions}
              value={userOptions.find((o) => o.value === form.user) || null}
              onChange={(selected) =>
                setForm((prev) => ({ ...prev, user: selected?.value || "" }))
              }
              placeholder="Select Traveler"
              isClearable
              isSearchable
              required
            />
          </div>

          {/* Trip Type (kept intact) */}
          <div className="col-md-4">
            <label>Trip Type</label>
            <select
              className="form-control"
              value={form.tripType}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, tripType: e.target.value }))
              }
              required
            >
              <option value="oneway">One Way</option>
              <option value="round">Round Trip</option>
            </select>
          </div>

          {/* Pax */}
          <div className="col-md-4">
            <label>Number of Passengers</label>
            <input
              type="number"
              className="form-control"
              placeholder="Number of Passengers"
              value={form.pax}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, pax: e.target.value }))
              }
              required
            />
          </div>

          {/* Empty Leg selector - shows only when admin chooses 'empty_legs' or if emptyLegs exist */}
          <div className="col-md-12">
            <label>Booking Type</label>
            <select
              className="form-control"
              value={form.bookingType}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, bookingType: e.target.value }))
              }
              required
            >
              <option value="normal">Normal Booking</option>
              <option value="empty_legs">Empty Legs</option>
            </select>
          </div>

          {form.bookingType === "empty_legs" && (
            <div className="col-md-12">
              <label>Select Empty Leg Deal</label>
              <Select
                options={emptyLegs}
                onChange={handleEmptyLegSelect}
                value={
                  emptyLegs.find((o) => o.value === form.emptyLegId) || null
                }
                placeholder="Search and select an Empty Leg deal"
                isClearable
              />
            </div>
          )}

          {/* From Airport */}
          <div className="col-md-6">
            <label>From Airport</label>
            <AsyncSelect
              cacheOptions
              loadOptions={loadAirportOptions}
              defaultOptions={false}
              value={
                form.fromPlace
                  ? {
                      value: form.fromPlace,
                      label: getAirportLabel(form.fromPlace),
                    }
                  : null
              }
              onChange={(selected) =>
                setForm((prev) => ({
                  ...prev,
                  fromPlace: selected?.value || "",
                }))
              }
              placeholder="Search From Airport"
              isClearable
              isDisabled={form.bookingType === "empty_legs"}
            />
          </div>

          {/* To Airport */}
          <div className="col-md-6">
            <label>To Airport</label>
            <AsyncSelect
              cacheOptions
              loadOptions={loadAirportOptions}
              defaultOptions={false}
              value={
                form.toPlace
                  ? {
                      value: form.toPlace,
                      label: getAirportLabel(form.toPlace),
                    }
                  : null
              }
              onChange={(selected) =>
                setForm((prev) => ({ ...prev, toPlace: selected?.value || "" }))
              }
              placeholder="Search To Airport"
              isClearable
              isDisabled={form.bookingType === "empty_legs"}
            />
          </div>

          {/* Travel Date & Time */}
          <div className="col-md-6">
            <label>Departure Date</label>
            <input
              type="date"
              className="form-control"
              value={form.travelDate}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, travelDate: e.target.value }))
              }
              required
              disabled={form.bookingType === "empty_legs"}
            />
          </div>

          <div className="col-md-6">
            <label>Departure Time</label>
            <input
              type="time"
              className="form-control"
              value={form.travelTime}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, travelTime: e.target.value }))
              }
              required
              disabled={form.bookingType === "empty_legs"}
            />
          </div>

          {/* Return Date & Time (only for round trip) */}
          {form.tripType === "round" && (
            <>
              <div className="col-md-6">
                <label>Return Date</label>
                <input
                  type="date"
                  className="form-control"
                  value={form.returnDate}
                  onChange={(e) =>
                    setForm((prev) => ({ ...prev, returnDate: e.target.value }))
                  }
                  required
                />
              </div>
              <div className="col-md-6">
                <label>Return Time</label>
                <input
                  type="time"
                  className="form-control"
                  value={form.returnTime}
                  onChange={(e) =>
                    setForm((prev) => ({ ...prev, returnTime: e.target.value }))
                  }
                  required
                />
              </div>
            </>
          )}

          {/* Flight Type */}
          <div className="col-md-6">
            <label>Flight Type</label>
            <Select
              options={flightOptions}
              value={
                flightOptions.find((o) => o.value === form.flightType) || null
              }
              onChange={(selected) =>
                setForm((prev) => ({
                  ...prev,
                  flightType: selected?.value || "",
                }))
              }
              placeholder="Select Flight Type"
              isClearable
              isSearchable
              required
              isDisabled={form.bookingType === "empty_legs"}
            />
          </div>

          {/* Total Amount */}
          <div className="col-md-6">
            <label>Total Amount</label>
            <input
              type="number"
              className="form-control"
              placeholder="Total Amount"
              value={form.totalAmount}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, totalAmount: e.target.value }))
              }
              required
            />
          </div>

          {/* Remarks */}
          <div className="col-md-12">
            <label>Remarks</label>
            <textarea
              className="form-control"
              rows="2"
              placeholder="Remarks"
              value={form.remarks}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, remarks: e.target.value }))
              }
            />
          </div>

          {/* Status */}
          <div className="col-md-6">
            <label>Status</label>
            <select
              className="form-control"
              value={form.status}
              onChange={(e) =>
                setForm((prev) => ({ ...prev, status: e.target.value }))
              }
              required
            >
              <option value="pending">Pending</option>
              <option value="confirmed_payment_pending">
                Confirmed (Payment Pending)
              </option>
              <option value="confirmed">Confirmed</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </div>

          {/* Buttons */}
          <div className="col-md-12 d-flex gap-2 mt-3">
            <button
              type="submit"
              className="btn btn-success"
              disabled={isLoading}
            >
              {isLoading
                ? "Processing..."
                : booking
                ? "Update Booking"
                : "Create Booking"}
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => setShowForm(false)}
              disabled={isLoading}
            >
              Cancel
            </button>
          </div>
        </div>
      </form>

      {showEmptyLegModal && (
        <EmptyLegModal
          legs={emptyLegsModalList}
          onClose={() => {
            setShowEmptyLegModal(false);
            setShowForm(false);
          }}
        />
      )}
    </>
  );
};

export default AddBookingForm;
