import React, { useState, useEffect } from "react";
import toast from "react-hot-toast";
import { useAddUserMutation, useGetRolesQuery } from "../../redux/api/adminApi";
import { useRegisterMutation } from "../../redux/api/authApi";

const AddUserForm = ({ onClose, refetch }) => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    role: "",
  });

  const { data: roleData } = useGetRolesQuery();
  const [addUser, { isLoading }] = useRegisterMutation();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addUser(form).unwrap();
      toast.success("User added successfully");
      onClose();
      refetch();
    } catch (err) {
      toast.error(err?.data?.message || "Failed to add user");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="border p-3 mb-3">
      <h5>Add New User</h5>
      <div className="row">
        <div className="col-md-3 mb-2">
          <input
            className="form-control"
            placeholder="Name"
            name="name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-3 mb-2">
          <input
            className="form-control"
            placeholder="Email"
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-3 mb-2">
          <input
            className="form-control"
            placeholder="Password"
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-3 mb-2">
          <select
            className="form-select"
            name="role"
            value={form.role}
            onChange={handleChange}
            required
          >
            <option value="">Select Role</option>
            {roleData?.map((r) => (
              <option key={r._id} value={r._id}>
                {r.name}
              </option>
            ))}
          </select>
        </div>
      </div>
      <button className="btn btn-success me-2" disabled={isLoading}>
        Add User
      </button>
      <button className="btn btn-secondary" onClick={onClose} type="button">
        Cancel
      </button>
    </form>
  );
};

export default AddUserForm;
