import React, { useState } from "react";
import {
  useDeleteBookingMutation,
  useGetBookingsQuery,
} from "../../redux/api/bookingApi";
import { Link } from "react-router-dom";
import AddBookingForm from "./AddBookingForm";

const BookingList = () => {
  const [showForm, setShowForm] = useState(false);
  const [editBooking, setEditBooking] = useState(null);
  const { data: bookings, isLoading, refetch } = useGetBookingsQuery();
  const [deleteBooking] = useDeleteBookingMutation();

  const handleEdit = (booking) => {
    setEditBooking(booking);
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setEditBooking(null);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Bookings</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditBooking(null); // reset to add mode
            setShowForm(!showForm);
          }}
        >
          {showForm ? "Close Form" : "Add Booking"}
        </button>
      </div>

      {showForm && (
        <AddBookingForm
          setShowForm={handleCloseForm}
          refetch={refetch}
          booking={editBooking} // send selected booking if edit
        />
      )}

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <table className="table table-bordered mt-4">
          <thead>
            <tr>
              <th>ID</th>
              <th>Traveler</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>From–To</th>
              <th>Date/Time</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {bookings?.map((b) => (
              <tr key={b._id}>
                <td>{b._id.slice(-5)}</td>
                <td>{b.user?.name}</td>
                <td>{b.user?.email}</td>
                <td>{b.user?.mobile}</td>
                <td>
                  {b.fromPlace} → {b.toPlace}
                </td>
                <td>
                  {b.travelDate} {b.travelTime}
                </td>
                <td>
                  <span
                    className={`badge bg-${
                      b.status === "confirmed"
                        ? "success"
                        : b.status === "cancelled"
                        ? "danger"
                        : "warning"
                    }`}
                  >
                    {b.status}
                  </span>
                </td>
                <td>
                  <Link
                    to={`/admin/bookings/${b._id}`}
                    className="btn btn-sm btn-outline-primary me-2"
                  >
                    View
                  </Link>
                  <button
                    className="btn btn-sm btn-outline-success"
                    onClick={() => handleEdit(b)}
                  >
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default BookingList;
