import React, { useEffect, useState, useRef } from "react";
import {
  useCreateBookingMutation,
  useUpdateBookingMutation,
} from "../../redux/api/bookingApi";
import { useGetUsersQuery } from "../../redux/api/adminApi";
import { useGetFlightTypesQuery } from "../../redux/api/flightTypeApi";
import { useGetEmptyLegsQuery } from "../../redux/api/emptyLegApi"; // ⭐ Empty legs
import AsyncSelect from "react-select/async";
import Select from "react-select";
import toast from "react-hot-toast";
import { airports } from "airports-json";

const getAirportLabel = (iata) => {
  if (!iata) return "";
  const airport = airports.find((a) => a.iata === iata || a.iata_code === iata);
  return airport
    ? `${airport.name} (${airport.iata || airport.iata_code}) - ${
        airport.municipality || "Unknown City"
      }`
    : iata;
};

const AddBookingForm = ({ setShowForm, refetch, booking }) => {
  const formRef = useRef(null);

  // ⭐ NO EMPTY LEG PRICE — USING ONLY totalAmount
  const [form, setForm] = useState({
    user: "",
    bookingType: "normal", // normal | empty_legs
    emptyLegId: "",

    fromPlace: "",
    toPlace: "",
    travelDate: "",
    travelTime: "",
    returnDate: "",
    returnTime: "",
    tripType: "oneway",

    flightType: "",
    pax: "",
    status: "pending",
    remarks: "",
    totalAmount: "",
  });

  const { data: users } = useGetUsersQuery();
  const { data: flightTypes } = useGetFlightTypesQuery();
  const { data: emptyLegsRaw } = useGetEmptyLegsQuery();
  const emptyLegs = emptyLegsRaw?.emptyLegs || [];

  const [createBooking, { isLoading: creating }] = useCreateBookingMutation();
  const [updateBooking, { isLoading: updating }] = useUpdateBookingMutation();
  const isLoading = creating || updating;

  // ⭐ Prefill when editing
  useEffect(() => {
    if (booking) {
      setForm({
        user: booking.user?._id || "",
        bookingType: booking.bookingType || "normal",
        emptyLegId: booking.emptyLegId || "",

        fromPlace: booking.fromPlace,
        toPlace: booking.toPlace,
        travelDate: booking.travelDate,
        travelTime: booking.travelTime,
        returnDate: booking.returnDate,
        returnTime: booking.returnTime,
        tripType: booking.tripType,

        flightType: booking.flightType?._id,
        pax: booking.pax,
        status: booking.status,
        remarks: booking.remarks,
        totalAmount: booking.totalAmount,
      });

      formRef.current?.scrollIntoView({ behavior: "smooth" });
    }
  }, [booking]);

  // ⭐ Airport search
  const searchAirports = (inputValue) => {
    if (!inputValue) return [];
    return airports
      .filter(
        (a) =>
          a.name?.toLowerCase().includes(inputValue.toLowerCase()) ||
          a.municipality?.toLowerCase().includes(inputValue.toLowerCase()) ||
          a.iata_code?.toLowerCase().includes(inputValue.toLowerCase())
      )
      .slice(0, 20)
      .map((a) => ({
        value: a.iata_code,
        label: `${a.name} (${a.iata_code}) - ${
          a.municipality || "Unknown City"
        }`,
      }));
  };

  const loadAirportOptions = (input, callback) => {
    setTimeout(() => callback(searchAirports(input)), 300);
  };

  // ⭐ Data for dropdowns
  const userOptions =
    users?.users?.map((u) => ({
      value: u._id,
      label: `${u.name} (${u.email})`,
    })) || [];

  const flightOptions =
    flightTypes?.flightTypes?.map((f) => ({
      value: f._id,
      label: `${f.name} (${f.capacity} seats)`,
    })) || [];

  const emptyLegOptions =
    emptyLegs.map((leg) => ({
      value: leg._id,
      label: `${leg.from} → ${leg.to} - ₹${(
        leg.offerPrice || leg.exactPrice
      ).toLocaleString()}`,
      leg,
    })) || [];

  // ⭐ When selecting an empty leg
  const onSelectEmptyLeg = (opt) => {
    if (!opt) return;

    const leg = opt.leg;

    setForm((prev) => ({
      ...prev,
      emptyLegId: leg._id,

      fromPlace: leg.from,
      toPlace: leg.to,
      travelDate: leg.date,
      travelTime: leg.time,

      flightType: leg.flightType?._id || "",
      totalAmount: leg.offerPrice || leg.exactPrice,
    }));
  };

  // ⭐ Submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const payload = {
        ...form,
        totalAmount: Number(form.totalAmount),
      };

      if (booking) {
        await updateBooking({ id: booking._id, data: payload }).unwrap();
      } else {
        await createBooking(payload).unwrap();
      }

      toast.success(
        booking
          ? "Booking Updated Successfully"
          : "Booking Created Successfully"
      );

      refetch();
      setShowForm(false);
    } catch (err) {
      console.error(err);
      toast.error("Failed to save booking");
    }
  };

  // ---------- UI ----------
  return (
    <>
      {isLoading && (
        <div className="loading-overlay">
          <div className="spinner-border text-primary"></div>
        </div>
      )}

      <form
        ref={formRef}
        onSubmit={handleSubmit}
        className="border p-3 bg-light"
      >
        <div className="row g-3">
          {/* Traveler */}
          <div className="col-md-4">
            <label>Traveler</label>
            <Select
              options={userOptions}
              value={userOptions.find((o) => o.value === form.user)}
              onChange={(o) => setForm((p) => ({ ...p, user: o?.value || "" }))}
              isClearable
              required
            />
          </div>

          {/* Booking Type */}
          <div className="col-md-4">
            <label>Booking Type</label>
            <select
              className="form-control"
              value={form.bookingType}
              onChange={(e) =>
                setForm((p) => ({ ...p, bookingType: e.target.value }))
              }
            >
              <option value="normal">Normal (Fly Now)</option>
              <option value="empty_legs">Empty Legs</option>
            </select>
          </div>

          {/* Pax */}
          <div className="col-md-4">
            <label>Passengers</label>
            <input
              type="number"
              className="form-control"
              value={form.pax}
              onChange={(e) => setForm((p) => ({ ...p, pax: e.target.value }))}
              required
            />
          </div>

          {/* Empty Leg Selector */}
          {form.bookingType === "empty_legs" && (
            <div className="col-md-12">
              <label>Select Empty Leg Deal</label>
              <Select
                options={emptyLegOptions}
                onChange={onSelectEmptyLeg}
                placeholder="Choose an Empty Leg Deal"
              />
            </div>
          )}

          {/* FROM Airport */}
          <div className="col-md-6">
            <label>From Airport</label>
            <AsyncSelect
              isDisabled={form.bookingType === "empty_legs"}
              cacheOptions
              loadOptions={loadAirportOptions}
              defaultOptions={false}
              value={
                form.fromPlace
                  ? {
                      value: form.fromPlace,
                      label: getAirportLabel(form.fromPlace),
                    }
                  : null
              }
              onChange={(s) =>
                setForm((p) => ({ ...p, fromPlace: s?.value || "" }))
              }
            />
          </div>

          {/* TO Airport */}
          <div className="col-md-6">
            <label>To Airport</label>
            <AsyncSelect
              isDisabled={form.bookingType === "empty_legs"}
              cacheOptions
              loadOptions={loadAirportOptions}
              defaultOptions={false}
              value={
                form.toPlace
                  ? {
                      value: form.toPlace,
                      label: getAirportLabel(form.toPlace),
                    }
                  : null
              }
              onChange={(s) =>
                setForm((p) => ({ ...p, toPlace: s?.value || "" }))
              }
            />
          </div>

          {/* Travel Date */}
          <div className="col-md-6">
            <label>Travel Date</label>
            <input
              type="date"
              className="form-control"
              value={form.travelDate}
              disabled={form.bookingType === "empty_legs"}
              onChange={(e) =>
                setForm((p) => ({ ...p, travelDate: e.target.value }))
              }
              required
            />
          </div>

          {/* Travel Time */}
          <div className="col-md-6">
            <label>Travel Time</label>
            <input
              type="time"
              className="form-control"
              value={form.travelTime}
              disabled={form.bookingType === "empty_legs"}
              onChange={(e) =>
                setForm((p) => ({ ...p, travelTime: e.target.value }))
              }
              required
            />
          </div>

          {/* Return Trip */}
          {form.tripType === "round" && (
            <>
              <div className="col-md-6">
                <label>Return Date</label>
                <input
                  type="date"
                  className="form-control"
                  value={form.returnDate}
                  onChange={(e) =>
                    setForm((p) => ({ ...p, returnDate: e.target.value }))
                  }
                />
              </div>

              <div className="col-md-6">
                <label>Return Time</label>
                <input
                  type="time"
                  className="form-control"
                  value={form.returnTime}
                  onChange={(e) =>
                    setForm((p) => ({ ...p, returnTime: e.target.value }))
                  }
                />
              </div>
            </>
          )}

          {/* Flight Type */}
          <div className="col-md-6">
            <label>Flight Type</label>
            <Select
              isDisabled={form.bookingType === "empty_legs"}
              options={flightOptions}
              value={flightOptions.find((o) => o.value === form.flightType)}
              onChange={(o) =>
                setForm((p) => ({ ...p, flightType: o?.value || "" }))
              }
              isClearable
              required
            />
          </div>

          {/* Total Amount */}
          <div className="col-md-6">
            <label>Total Amount</label>
            <input
              type="number"
              className="form-control"
              value={form.totalAmount}
              onChange={(e) =>
                setForm((p) => ({ ...p, totalAmount: e.target.value }))
              }
              required
            />
          </div>

          {/* Remarks */}
          <div className="col-md-12">
            <label>Remarks</label>
            <textarea
              className="form-control"
              value={form.remarks}
              onChange={(e) =>
                setForm((p) => ({ ...p, remarks: e.target.value }))
              }
            />
          </div>

          {/* Booking Status */}
          <div className="col-md-12">
            <label>Status</label>
            <select
              className="form-control"
              value={form.status}
              onChange={(e) =>
                setForm((p) => ({ ...p, status: e.target.value }))
              }
            >
              <option value="pending">Pending</option>
              <option value="confirmed_payment_pending">
                Confirmed (Payment Pending)
              </option>
              <option value="confirmed">Confirmed</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </div>

          {/* Buttons */}
          <div className="col-md-12 d-flex gap-2 mt-3">
            <button
              type="submit"
              className="btn btn-success"
              disabled={isLoading}
            >
              {booking ? "Update Booking" : "Create Booking"}
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              disabled={isLoading}
              onClick={() => setShowForm(false)}
            >
              Cancel
            </button>
          </div>
        </div>
      </form>
    </>
  );
};

export default AddBookingForm;
